    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
    //Tenis valor = new Tenis();
   Tenis.player.setPuntos(Tenis.jugador_a);  
    
        if(Tenis.player.ganador() != 1 && Tenis.player.getTirosJugador_a() < 4){
            //jLabel2.setText(puntos_nombre_en[Tenis.player.getTirosJugador_a()]);
            jLabel2.setText(Tenis.mi_idioma[Tenis.player.getTirosJugador_a()]);
        }
       
        if(Tenis.player.getTirosJugador_a()-Tenis.player.getTirosJugador_b() == 1 && Tenis.player.getTirosJugador_a() > 3){
            jLabel2.setText(Tenis.mi_idioma[4] + " Jugador A");
            jLabel4.setText(null);
        }
        
        if(Tenis.player.ganador() == 1){
            jLabel2.setText("ganador A");
            jLabel4.hide();
            jButton1.setVisible(false);
            jButton2.setVisible(false);
        }
        
        if(Tenis.player.ganador() == 3){
            jLabel4.setText("Deuce");
            jLabel2.setText("Deuce");
            
        }
        
    
    // TODO add your handling code here:
    }//GEN-LAST:event_jButton1ActionPerformed

    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed
    //Tenis valor = new Tenis();
    Tenis.player.setPuntos(Tenis.jugador_b);         
     if(Tenis.player.ganador() != 2 && Tenis.player.getTirosJugador_b() < 4){
            jLabel4.setText(Tenis.mi_idioma[Tenis.player.getTirosJugador_b()]);
        }
     
     if(Tenis.player.getTirosJugador_b()-Tenis.player.getTirosJugador_a() == 1 && Tenis.player.getTirosJugador_b() > 3){
            jLabel2.setText(Tenis.mi_idioma[4] + " Jugador B");
            jLabel4.setText(null);
        }
     
     if(Tenis.player.ganador() == 2){
            jLabel4.setText("ganador B");
            jLabel2.hide();
            jButton1.setVisible(false);
            jButton2.setVisible(false);
        }
     
     if(Tenis.player.ganador() == 3){
            jLabel4.setText("Deuce");
            jLabel2.setText("Deuce");
        }
    // TODO add your handling code here:
    }//GEN-LAST:event_jButton2ActionPerformed

